import React from "react";

import { styled } from "styled-components";
const OrderConfirmed = () => {
  const PageDiv = styled.div`
    height: 100vh;
    background-color: #ce2829;
    color: white;
    padding: 3rem;
  `;
  const ContainerDiv = styled.div`
    width: 45%;
    margin: auto;
    text-align: center;
  `;
  const SummaryDiv = styled.div`
    border: 0.25rem solid white;
    width: 45%;
    display: flex;
    flex-direction: column;
    margin: auto;
  `;
  const Summary1 = styled.div`
    display: flex;
    justify-content: space-evenly;
  `;
  const Summary2 = styled.div`
    display: flex;
    justify-content: space-evenly;
  `;
  const DetailsDiv = styled.div`
    display: flex;
    justify-content: flex-start;
    gap: 0.25rem;
    & p:nth-child(1) {
      font-weight: bold;
    }
  `;
  const Div = styled.div`
    display: flex;
    flex-direction: column;
    margin: auto;
  `;
  return (
    <PageDiv>
      <ContainerDiv className="container">
        <p>Lezzetin yolda</p>
        <p>SİPARİŞ ALINDI</p>
        <hr></hr>
        <h5>pizzaname</h5>
        <Div>
          <DetailsDiv>
            <p>Boyut:</p>
            <p>Boyut</p>
          </DetailsDiv>
          <DetailsDiv>
            <p>Hamur:</p>
            <p>hamur</p>
          </DetailsDiv>
          <DetailsDiv>
            <p>Ek Malzemeler:</p>
            <p>ekmalzemeler</p>
          </DetailsDiv>
        </Div>
        <SummaryDiv>
          <h6>Sipariş Toplamı</h6>
          <Summary1>
            <p>Seçimler</p>
            <p>55 tl</p>
          </Summary1>
          <Summary2>
            <p>Toplam</p>
            <p>100 tl</p>
          </Summary2>
        </SummaryDiv>
      </ContainerDiv>
    </PageDiv>
  );
};
export default OrderConfirmed;
