import React from "react";
import Footer from "../layout/Footer";
import FavoriteMenu from "../Components/FavoriteMenu";
import MenuCard from "../Components/MenuCard";
import { useState } from "react";

export default function SeceneklerPage({ menuList }) {
  const [type, setType] = useState("");

  const clickHandler = (e) => {
    const menuId = e.target.id;
    e.target.name === "img"
      ? setType(e.target.parentNode.name)
      : setType(e.target.name);
    e.target.name === "img"
      ? (e.target.parentNode.className =
          e.target.parentNode.id === menuId ? "bg-dark" : "bg-light")
      : (e.target.className = e.target.id === menuId ? "bg-dark" : "bg-light");
  };
  return (
    <div>
      <FavoriteMenu clickHandler={clickHandler} />
      <MenuCard type={type} menuList={menuList} />
      <Footer />
    </div>
  );
}
