import React from "react";
import { Route, Switch } from "react-router-dom";
import HomePage from "../pages/HomePage";
import SiparisPage from "../pages/SiparisPage";
import SeceneklerPage from "../pages/SeceneklerPage";
import Orderconfirmed from "../Components/Orderconfirmed";
import { useParams } from "react-router-dom";
import { MenuList } from "../helper/MenuList";
import { useEffect, useState } from "react";

const PageContent = () => {
  const [urun, setUrun] = useState({});
  const { menuItemId } = useParams();

  useEffect(() => {
    const food = MenuList.find((menu) => menu.id === menuItemId);
    setUrun(food);
    console.log(urun);
  }, [menuItemId, MenuList]);

  return (
    // Page Componentleri
    <div>
      <Switch>
        <Route path="/" exact>
          <HomePage />
        </Route>
        <Route path="/secenekler" exact>
          <SeceneklerPage />
        </Route>
        <Route path="/siparis-ver/:menuItemId" exact>
          <SiparisPage urun={urun} />
        </Route>
        <Route path="/siparis-alindi" exact>
          <Orderconfirmed />
        </Route>
      </Switch>
    </div>
  );
};

export default PageContent;
