import React from "react";
import Footer from "../layout/Footer";
import SiparisVer from "../Components/SiparisVer";
import SiparisForm from "../Components/SiparisForm";
import "../css/siparis.css";
import { useParams } from "react-router-dom";
import { MenuList } from "../helper/MenuList";
import { useEffect, useState } from "react";
const [urun, setUrun] = useState({});

const SiparisPage = ({ urun }) => {
  const { menuItemId } = useParams();
  useEffect(() => {
    const food = MenuList.find((menu) => menu.id == menuItemId);
    setUrun(food);
  }, [menuItemId, MenuList]);

  return (
    <>
      <SiparisVer urun={urun} />
      <SiparisForm
        urun
        onSubmitHandler={onSubmitHandler}
        inputChangeHandler={inputChangeHandler}
        errors={errors}
        ekMalzemeler={ekMalzemeler}
        formData={formData}
        isValid={isValid}
        urun={urun}
        total={total}
        sayacArttir={sayacArttir}
        sayacAzalt={sayacAzalt}
        sayac={sayac}
      />
      <Footer />
    </>
  );
};

export default SiparisPage;
