import React from "react";
import { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import { Button } from "reactstrap";
import { MenuList } from "../helper/MenuList";
import "../css/siparis.css";
import SiparisInfo from "./SiparisInfo";
const UrunDetayiPage = () => {
  const [urun, setUrun] = useState({});
  const { menuItemId } = useParams();
  const history = useHistory();

  const navBack = () => {
    // todo: go back previous URL in history
    history.goBack();
    // history.push("/products");
  };

  useEffect(() => {
    const food = MenuList.find((menu) => menu.id == menuItemId);
    setUrun(food);
  }, [menuItemId, MenuList]);

  console.log(urun.price);
  console.log(urun.point);
  console.log(urun.orderNum);
  //

  return (
    <div>
      <div>
        <Button color="secondary" onClick={navBack}>
          {"<"}
        </Button>
        <div className="d-flex flex-column">
          <div className="py-2 secilenMenu">
            <h4>{urun.urunName}</h4>
            <div className="py-4 d-flex gap-5 justify-content-between">
              <h2 className="fw-bold ">{urun.price}</h2>
              <h6 className="text-muted">{urun.point}</h6>
              <h6 className="text-muted">{urun.orderNum}</h6>
            </div>
            <p className="description text-wrap fs-5 text-muted">
              {urun.description}
            </p>
          </div>
        </div>
      </div>
      <SiparisInfo urunName={urun.urunName} />
    </div>
  );
};

export default UrunDetayiPage;
