import React from "react";
import Footer from "../layout/Footer";
import MenuCard from "../Components/MenuCard";
import { useState } from "react";
import Menu from "../Components/Menu";

export default function SeceneklerPage() {
  const [type, setType] = useState("");

  const clickHandler = (e) => {
    let toggle1 = e.target.classList;
    let toggle2 = e.target.parentNode.classList;
    e.target.name === "img"
      ? setType(e.target.parentNode.name)
      : setType(e.target.name);
    e.target.name === "img"
      ? toggle2.toggle("active")
      : toggle1.toggle("active");
  };
  return (
    <div>
      <Menu clickHandler={clickHandler} />
      <MenuCard type={type} />
      <Footer />
    </div>
  );
}
