import { useEffect, useState } from "react";
import { useParams, useHistory } from "react-router-dom";
import { Button } from "reactstrap";
import MenuList from "../helper/MenuList";

const UrunDetayiPage = ({ products }) => {
  const [item, setItem] = useState(null);
  const { menuItemId } = useParams();
  const history = useHistory();

  const navBack = () => {
    // todo: go back previous URL in history
    history.goBack();
    // history.push("/products");
  };

  useEffect(() => {
    const item = MenuList?.find((menu) => menu.id === menuItemId);
    setProduct(item);
  }, [menuItemId, MenuList]);

  return (
    <div>
      <h2>
        <Button color="secondary" onClick={navBack}>
          {"<"}
        </Button>
      </h2>
      <hr />
      <div className="d-flex flex-column">
        <img alt="Sample" src={product?.img} />
        <h2>{product?.name}</h2>
        <p>{product?.description}</p>
        <p>{product?.price}</p>
        <p>{product?.stock}</p>
      </div>
    </div>
  );
};

export default UrunDetayiPage;
