import React, { useState } from "react";
import "../css/home.css";
import { Link } from "react-router-dom";
import Menu from "./Menu";
import HighlightMenu from "./HighlightMenu";
import FavoriteMenu from "./FavoriteMenu";
import MenuCard from "./MenuCard";
const Home = () => {
  const [type, setType] = useState("");
  const [menu, setMenu] = useState([]);

  useEffect(() => {
    setMenu([...MenuList]);
  }, []);

  const filterMenu = MenuList.filter((menuItem) => {
    return menuItem.category === type;
  });
  console.log(filterMenu);
  setMenu(filterMenu);
  const clickHandler = (e) => {
    e.target.name === "img"
      ? setType(e.target.parentNode.name)
      : setType(e.target.name);
    e.target.name === "img"
      ? (e.target.parentNode.className = "active")
      : (e.target.className = "active");
  };
  return (
    <div>
      <div className="home-page">
        <div className="container">
          <p className="p-yellow">Lezzeti Kaçırma</p>
          <p className="p-white">KOD ACIKTIRIR</p>
          <p className="p-white">PİZZA DOYURUR</p>
          <Link to="/siparis-ver" exact>
            <button className="btn btn-warning rounded-pill px-5">
              ACIKTIM
            </button>
          </Link>
        </div>
      </div>
      <div>
        <Menu />
      </div>
      <div>
        <HighlightMenu />
      </div>
      <div>
        <FavoriteMenu clickHandler={clickHandler} />
      </div>
      <div>
        <MenuCard type={type} />
      </div>
    </div>
  );
};
export default Home;
