import React, { useState } from "react";
import { EkMalzemeler } from "../helper/EkMalzemeler";
const foodDataInitial = {
  siparisNotu: "",
  boyut: "",
  hamur: "",
  ekMalzemeler: [],
};

const siparisAlindiInitial = {
  pizzaName: "",
  boyut: "",
  hamur: "",
  ekMalzemeler: [],
};

export default function SiparisForm({ urun }) {
  const [foodData, setFoodData] = useState(foodDataInitial);
  const [sayac, setSayac] = useState(1);
  const [disable, setDisable] = useState(true);
  // const [siparisAlindi, setSiparisAlindi] = useState(siparisAlindiInitial);

  const inputChangeHandler = (event) => {
    if (event.target.name === "ekMalzemeler") {
      let copyFoodData = { ...foodData };

      if (event.target.checked) {
        copyFoodData.ekMalzemeler.push(event.target.value);
      } else {
        copyFoodData.ekMalzemeler = copyFoodData.ekMalzemeler.filter(
          (malzeme) => malzeme !== event.target.value
        );
      }
      if (copyFoodData.ekMalzemeler.length === 10) {
        setDisable(!disable);
      }
      setFoodData(copyFoodData);
    } else {
      setFoodData(() => ({
        ...foodData,
        [event.target.name]: event.target.value,
      }));
    }
  };

  const onSubmitHandler = (e) => {
    e.preventDefault();
    console.log("sumbitted");
  };

  const arttirAction = () => {
    setSayac(sayac + 1);
  };

  const azaltAction = () => {
    setSayac(sayac - 1);
  };

  return (
    <div className="siparisContainer">
      <form className="mb-5 py-5" onSubmit={onSubmitHandler}>
        <form className="d-flex justify-content-between">
          <form>
            <h6 className="required fw-bold">Boyut Seç</h6>
            <div className="boyutSec w-100 d-flex gap-2">
              <input
                onChange={inputChangeHandler}
                id="radio-S"
                className="boyut-input"
                type="radio"
                name="boyut"
                value="S"
              />
              <label className="boyut-label" htmlFor="radio-S">
                S
              </label>
              <input
                onChange={inputChangeHandler}
                id="radio-M"
                className="boyut-input"
                type="radio"
                name="boyut"
                value="M"
              />
              <label className="boyut-label" htmlFor="radio-M">
                M
              </label>
              <input
                onChange={inputChangeHandler}
                id="radio-L"
                className="boyut-input"
                name="boyut"
                type="radio"
                value="L"
              />
              <label className="boyut-label" htmlFor="radio-L">
                L
              </label>
            </div>
          </form>
          <form>
            <h6 className="required fw-bold">Hamur Seç</h6>
            <select
              className="formSelect"
              aria-label="Default select example"
              name="hamur"
              onChange={inputChangeHandler}
            >
              <option selected>- Hamur Kalınlığını Seç - </option>
              <option value="kalın">Kalın</option>
              <option value="orta">Orta</option>
              <option value="ince">İnce</option>
            </select>
          </form>
        </form>

        <h6 className="fw-bold mt-5">Ek Malzemeler</h6>
        <h6 className="text-muted my-4">
          En fazla 10 malzeme seçebilirsiniz. 5tl
        </h6>
        <form className="mb-5 w-100 d-flex gap-4 flex-wrap fw-medium">
          {EkMalzemeler.map((EkMalzeme) => (
            <form className="w-25">
              <label className="d-flex align-items-center">
                <input
                  onChange={inputChangeHandler}
                  className="check"
                  type="checkbox"
                  name="ekMalzemeler"
                  value={EkMalzeme}
                  disabled={!disable}
                />
                {EkMalzeme}
              </label>
            </form>
          ))}
        </form>

        <form>
          <label className="d-flex flex-column fw-bold">
            Sipariş Notu
            <input
              onChange={inputChangeHandler}
              className="siparisNotuInput"
              type="text"
              name="siparisNotu"
              placeholder="Siparişine eklemek istediğin bir not var mı?"
            />
          </label>
        </form>

        <hr />

        <form className="siparisOzetiForm">
          <div className="siparisSayisi">
            <button onClick={azaltAction}>-</button>
            <label>{sayac}</label>
            <button onClick={arttirAction}>+</button>
          </div>
          <div className="siparisToplami">
            <div className="siparisOzetiContainer">
              <h6 className="p-4 fw-bold">Sipariş Toplamı</h6>
              <h6 className="d-flex justify-content-between ">
                <div className="px-4">
                  <p className="w-100 d-flex justify-content-between">
                    {urun.urunName}
                    <p>{urun.price} tl</p>
                  </p>
                  <p className="w-100 d-flex justify-content-between">
                    Boyut:<p>{foodData.boyut}</p>
                  </p>
                  <p className="w-100 d-flex justify-content-between">
                    Hamur:<p>{foodData.hamur}</p>
                  </p>
                  <p className="w-100 d-flex justify-content-between text-wrap">
                    Ek Malzemeler:{" "}
                    <p className="m-0 text-wrap">{foodData.ekMalzemeler}</p>
                  </p>
                </div>
              </h6>
              <h6 className="d-flex p-4 justify-content-between text-danger">
                Toplam
                <span>110.50 tl</span>
              </h6>
            </div>
            <button
              onClick={() => console.log(foodData)}
              className="btn-siparisVer"
              type="submit"
            >
              SİPARİŞ VER
            </button>
          </div>
        </form>
      </form>
    </div>
  );
}
