import React from "react";
import { styled } from "styled-components";
import "../css/order.css";
const OrderConfirmed = ({ boyut }) => {
  const PageDiv = styled.div`
    height: 100vh;
    background-color: #ce2829;
    color: white;
    padding: 15rem;
    padding-top: 0;
  `;
  const ContainerDiv = styled.div`
    width: 45%;
    margin: auto;
    text-align: center;
  `;
  const SummaryDiv = styled.div`
    border: 0.08rem solid white;
    width: 45%;
    display: flex;
    flex-direction: column;
    margin: auto;
    padding: 1rem;
  `;
  const Summary1 = styled.div`
    display: flex;
    justify-content: space-between;
  `;
  const Summary2 = styled.div`
    display: flex;
    justify-content: space-between;
  `;
  const DetailsDiv = styled.div`
    display: flex;
    justify-content: flex-start;
    gap: 0.25rem;
    & p:nth-child(1) {
      font-weight: bold;
    }
  `;
  const P = styled.p`
    font-family: "Barlow", sans-serif;
    font-size: 3rem;
  `;
  const Pl = styled.p`
    font-family: "Satisfy", cursive;
    color: #fdc913;
    margin-bottom: 0;
    font-size: 1.5rem;
  `;
  return (
    <div className="order">
      <PageDiv>
        <ContainerDiv className="container">
          <Pl>Lezzetin yolda</Pl>
          <P>SİPARİŞ ALINDI</P>
          <hr></hr>
          <h5 className="mb-5">pizzaname</h5>
          <div className="Div">
            <DetailsDiv>
              <p>Boyut:</p>
              <p>{boyut}</p>
            </DetailsDiv>
            <DetailsDiv>
              <p>Hamur:</p>
              <p>hamur</p>
            </DetailsDiv>
            <DetailsDiv>
              <p>Ek Malzemeler:</p>
              <p>ekmalzemeler</p>
            </DetailsDiv>
          </div>
          <SummaryDiv>
            <h6 className="mb-4 text-start">Sipariş Toplamı</h6>
            <Summary1>
              <p>Seçimler</p>
              <p>55 tl</p>
            </Summary1>
            <Summary2>
              <p>Toplam</p>
              <p>100 tl</p>
            </Summary2>
          </SummaryDiv>
        </ContainerDiv>
      </PageDiv>
    </div>
  );
};
export default OrderConfirmed;
